import React from 'react'


function BuddyChat(props) {
    return (
        <div className={'buddy-chat '+props.select}>
           <div className="message-number">
               <p>
                   {props.number}
               </p>
           </div>
           <div className="buddy-name">
               <h3>
                  {props.name}
               </h3>
           </div>
           <div className="closeBu">
               X
           </div>
           <style jsx> {`
               .buddy-chat { 
                   width:auto;
                   background:#f4f5f7;
                   margin-right:1vh;
                   padding:12px;
                   display:flex;
                   align-items:center;
                   border-radius:24px;
                   position:relative;
               }
               .buddy-chat:hover>.closeBu {
                   display:flex;
               }
               .closeBu {
                   position:absolute;
                   right:2%;
                   top:0%;
                   background:red;
                   color:white;
                   height:2vh;
                   width:2vh;
                   display:none;
                   justify-content:center;
                   align-items:center;
                   border-radius:50%;
               }
               .select {
                   background:white;
               }
               .message-number {
                 color:white;
                 height:2.4vh;
                 width:2.4vh;
                 display:flex;
                 justify-content:center;
                 align-items:center;
                 background:#238cff;
                 border-radius:50%;
                 margin:0 1vh;
               }
               .buddy-name {
                   font-size:1vh;
                   white-space: nowrap;
                   wdith:auto;
                   padding-right:1vh;
               }

                        `}
            </style>
        </div>
    )
}

export default BuddyChat
